import org.bukkit.Material;import org.bukkit.entity.Player;import org.bukkit.block.Sign;import org.bukkit.block.Block;import org.bukkit.block.BlockState;import java.util.Set;public class helper2{public String getFirstLine(Player p){if(p.getTargetBlock((Set<Material>) null,5).equals(null)){p.sendMessage("helper  l");return "l";}else{p.sendMessage("helper  gb");Block b = p.getTargetBlock((Set<Material>) null,5);if(b.getType().equals(Material.WALL_SIGN)){p.sendMessage("helper  tgs");Sign s = (Sign)b.getState();p.sendMessage("helper  rt");return s.getLine(0);}else{p.sendMessage("helper  nt");return "nt";}}}public void setLastLine(Player p,String s){if(p.getTargetBlock((Set<Material>) null,5).equals(null)){p.sendMessage("helper l");return;}else if(p.getTargetBlock((Set<Material>) null,5).getType().equals(Material.WALL_SIGN)){p.sendMessage("helper tgb");Block b = p.getTargetBlock((Set<Material>) null,5);Sign d = (Sign)b.getState();p.sendMessage("sg");d.setLine(3,s);d.update();}else{p.sendMessage("helper nt");return;}}}
